  --ring: hsl(217, 91%, 60%);
  --radius: 12px;
  --font-sans: Inter, system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
}

.dark {
  --background: hsl(220, 40%, 6%);
  --foreground: hsl(220, 10%, 98%);
  --card: hsl(220, 30%, 12%);
  --card-foreground: hsl(220, 10%, 95%);
  --popover: hsl(220, 30%, 12%);
  --popover-foreground: hsl(220, 10%, 95%);
  --primary: hsl(217, 91%, 60%);
  --primary-foreground: hsl(220, 10%, 98%);
  --secondary: hsl(220, 20%, 18%);
  --secondary-foreground: hsl(220, 10%, 90%);
  --muted: hsl(220, 20%, 15%);
  --muted-foreground: hsl(220, 10%, 70%);
  --accent: hsl(162, 88%, 40%);
  --accent-foreground: hsl(220, 10%, 98%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(220, 10%, 98%);
  --border: hsl(220, 20%, 20%);
  --input: hsl(220, 20%, 18%);
  --ring: hsl(217, 91%, 60%);
}

@layer base {
  * {
    @apply border-border scroll-smooth;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground overflow-x-hidden;
    background: linear-gradient(135deg, hsl(220, 40%, 6%) 0%, hsl(220, 35%, 8%) 100%);
  }
}

@layer utilities {
  .animated-bg {
    background: linear-gradient(-45deg, hsl(220, 40%, 6%), hsl(220, 35%, 8%), hsl(220, 30%, 10%), hsl(220, 25%, 12%));
    background-size: 400% 400%;
    animation: gradient 15s ease infinite;
  }

  .glow-border {
    position: relative;
  }

  .glow-border::before {
    content: '';
    position: absolute;
    inset: 0;
    padding: 2px;
    background: linear-gradient(45deg, var(--primary), var(--accent));
    border-radius: inherit;
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: xor;
    -webkit-mask-composite: xor;
  }

  .typing-animation {
    overflow: hidden;
    border-right: 3px solid var(--accent);
    white-space: nowrap;
    animation: typewriter 4s steps(40) infinite, blink-caret 0.75s step-end infinite;
  }

  .skill-bar {
    transition: width 2s ease-in-out;
  }

  .project-card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .project-card:hover {
    transform: translateY(-8px) scale(1.02);
  }

  .floating-shapes {
    position: absolute;
    width: 100%;
    height: 100%;
    overflow: hidden;
    pointer-events: none;
  }

  .floating-shape {
    position: absolute;
    opacity: 0.1;
    animation: float 6s ease-in-out infinite;
  }

  .floating-shape:nth-child(1) {
    top: 20%;
    left: 10%;
    animation-delay: 0s;
  }

  .floating-shape:nth-child(2) {
    top: 60%;
    right: 10%;
    animation-delay: 2s;
  }

  .floating-shape:nth-child(3) {
    bottom: 20%;
    left: 20%;
    animation-delay: 4s;
  }
}

@keyframes gradient {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes typewriter {
  0% { width: 0; }
  50% { width: 100%; }
  100% { width: 0; }
}

@keyframes blink-caret {
  from, to { border-color: transparent; }
  50% { border-color: var(--accent); }
}

@keyframes fadeInUp {
  0% { opacity: 0; transform: translateY(30px); }
  100% { opacity: 1; transform: translateY(0px); }
}

